import React,{useEffect,useReducer,useState} from 'react'
import axios from 'axios'
import '../styles/mystyle.scss'
function Sections() {
    const [source,setSource]=useState([])
    const [data,setData]=useState()
    const [compA,setCompA]=useState(2)

    // const [data,dispatch]=useReducer(reducer,initstate)
    useEffect(()=>{
        axios.get('../../chairs.json')
        .then(response=>setCompA(response.data.filter(chair=>chair.section === "A")))
        // .then(response=>setSource(response.data))
    },[])
    const showsource = ()=>{
        console.log(source)
        const placeA = source.filter(chair=>chair.section === "A")
        console.log(placeA)
        setCompA(12)
        console.log(compA)
        const placeB = source.filter(chair=>chair.section === "B")
        console.log(placeB)
        const placeC = source.filter(chair=>chair.section === "C")
        console.log(placeC)
        const placeD = source.filter(chair=>chair.section === "D")
        console.log(placeD)
    }
    return (
        <>
        <div className="space">
            <div className='header'>
                <button disabled> <h1>stage</h1> </button>
                <button onClick={showsource}>console source</button>
            </div>
            <div className='chair-containers'>
            <div className='closer'>

                <div className='section-b'>
                   {/* {placeB.map(chair=>(<div className='chair'>
                     <h6>{chair.number}</h6> 
                    
                    </div>))} */}
                    
                </div>
                <div className='section-a'>

                </div>
                <div className='section-c'>

                </div>
            </div>
                <div className='section-d'>

                </div>
            </div>
        </div>
        
        </>
    )
}

export default Sections
