import React, { useEffect, useReducer, useState } from 'react'
import axios from 'axios'
import '../styles/mystyle.scss'
import CompA from './CompA'
import CompB from './CompB'
import CompC from './CompC'
import CompD from './CompD'
function Sections() {
    const initdata = {
        chairs: [],
        error: "",
        count: 0,
        sum: 0
    }
    const reducer = (state, action) => {
        switch (action.type) {
            case ("success"): return { ...state, chairs: action.chairs }
            case ("failed"): return { ...state, error: action.error }
            case ("change-state"): return { ...state, chairs: action.chairs }
            case ("count"): return { ...state, sum: action.sum, count: action.count }
            default: return state
        }
    }
    const [data, dispatch] = useReducer(reducer, initdata)
    useEffect(() => {
        axios.get('../../chairs.json')
            .then((response) => dispatch({
                type: "success",
                chairs: response.data
            }))
            // setData(response.data)
            // setCompA(response.data.filter(chair => chair.section === "A"))
            // setCompB(response.data.filter(chair => chair.section === "B"))
            // setCompC(response.data.filter(chair => chair.section === "C"))
            // setCompD(response.data.filter(chair => chair.section === "D"))
            .catch((error) => dispatch({
                type: "failed",
                error: error.message
            }))
    }, [])
    const showsource = () => {
        // console.log(compA)
        // console.log(compB)
        // console.log(compC)
        // console.log(compD)
        console.log(data)

    }
    const clickhandler = (number) => {
        console.log(data.chairs.filter(chair => (chair.section === "A")))
        console.log(data.chairs[number - 1])
        switch (data.chairs[number - 1].state) {
            case "unselected": { data.chairs[number - 1].state = "selected" } break;
            case "selected": { data.chairs[number - 1].state = "reserved" } break;
            case "reserved": { data.chairs[number - 1].state = "count" } break;
            case "count": { alert("قبلا رزرو شده") } break;
        }
        dispatch({
            type:"change-state",
            chairs:data.chairs
        })
        if(data.chairs[number - 1].state = "reserved"){
            const reservedchairs = data.chairs.map(chair=>chair.state==="reserved");
            let count=reservedchairs.length+1;
            let sum = 0;
            

            dispatch({
                type:"reserved",
                count:count,
                sum:sum
            })
        }
    }
    return (
        <>
            <div className="space">
                <div className='header'>
                    <button disabled> <h1>stage</h1> </button>
                    <button onClick={showsource}>console source</button>
                    <button onClick={clickhandler}>console clickhandler</button>
                </div>
                <div className='chair-containers'>
                    <div className='closer'>
                        {data.chairs.map((chair) => (<button key={chair.number} onClick={() => { clickhandler(`${chair.number}`) }}>{chair.number}</button>))}
                        {/* <CompB chairs={compB} clickhandler={clickhandler} />
                        <CompA chairs={compA} clickhandler={clickhandler}/>
                        <CompC chairs={compC} clickhandler={clickhandler}/> */}
                    </div>
                    {/* <CompD chairs={compD} clickhandler={clickhandler}/> */}
                </div>
            </div>

        </>
    )
}

export default Sections
