import React from 'react'

function CompA(props) {
    const data=props.chairs;
    const clickHandler = (event)=>{
        const goal = event.target.innerText
        console.log(goal)
        event.target.style={background:"yellow"}
    }
    return (
        <div className='section-a'>
            {props.chairs.map(chair=>(<div className='chair' key={chair.number} onClick={clickHandler}>
                     {chair.number} 
                    </div>))}
        </div>
    )
}

export default CompA
